INSERT INTO `pages` (`id`, `syn`, `title`, `meta_keywords`, `meta_description`, `h1_title`, `params`, `content`, `active`) VALUES
(1, 'home', 'автозапчасти, автозапчасти польша,магазин автозапчасти польша, ', 'Магазин автозапчастей польша, купить автозапчасти из польши, интернет- магазин автозапчастей, электронные каталоги, подбор запчасти,запчасти купить польские,автозапчасти на иномарки', 'Вы можете самостоятельно купить автозапчасти оригинальные, а так же не оригинальные без больших временных затрат, выбрать для себя нужную цену.', 'Главная', '', '<p>\n	<strong>Преимущества интернет магазина</strong>&nbsp;<strong>EPARTS</strong></p>\n<p>\n	<b><span style="font-size: 9pt; font-family: Arial, sans-serif;">- Отслеживание состояния заказа автозапчастей онлайн</span></b><span style="font-size: 9pt; font-family: Arial, sans-serif;"> (</span><span lang="RU" style="font-size: 9pt; font-family: Arial, sans-serif;">в</span><span style="font-size: 9pt; font-family: Arial, sans-serif;"> сво</span><span lang="RU" style="font-size: 9pt; font-family: Arial, sans-serif;">ё</span><span style="font-size: 9pt; font-family: Arial, sans-serif;">м личном</span><span lang="RU" style="font-size: 9pt; font-family: Arial, sans-serif;"> кабинете вы отслеживаете на какой стадии готовности ваш товар).<o:p></o:p></span></p>\n<p>\n	<b><span style="font-size: 9pt; font-family: Arial, sans-serif;">- Разнообразные способы оплаты</span></b><span style="font-size: 9pt; font-family: Arial, sans-serif;"> (</span><span lang="RU" style="font-size: 9pt; font-family: Arial, sans-serif;">с любого банка или терминала Украины вы можете сделать оплату товара).<o:p></o:p></span></p>\n<p>\n	<b><span style="font-size: 9pt; font-family: Arial, sans-serif;">- Помощь в подборе оформлении и получении заказа,&nbsp;</span></b><b style="font-size: 12px;"><span style="font-size: 9pt; font-family: Arial, sans-serif;">закрепление персонального менеджера.</span></b></p>\n<p>\n	<b><span style="font-size: 9pt; font-family: Arial, sans-serif;">- Доставка в любую точку Украины&nbsp;</span></b><span lang="RU" style="font-size: 9pt; font-family: Arial, sa